import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [product, setProduct] = useState('');
  const [results, setResults] = useState(null);

  const analyzeProduct = async () => {
    const res = await axios.post('http://localhost:8000/analyze', { name: product });
    setResults(res.data);
  };

  const getSortedAspects = () => {
    if (!results) return [];
    return Object.entries(results).sort((a, b) => b[1].average_score - a[1].average_score);
  };

  const sorted = getSortedAspects();
  const best = sorted[0];
  const worst = sorted[sorted.length - 1];

  return (
    <div style={{ padding: 20 }}>
      <h2>Aspect-Based Sentiment Analyzer</h2>
      <input value={product} onChange={(e) => setProduct(e.target.value)} placeholder="Enter product name" />
      <button onClick={analyzeProduct}>Analyze</button>

      {results && (
        <div>
          <h3>Best Aspect: {best[0]} ({best[1].average_score})</h3>
          <h3>Worst Aspect: {worst[0]} ({worst[1].average_score})</h3>
          <hr />
          <ul>
            {sorted.map(([aspect, data]) => (
              <li key={aspect}>
                {aspect}: {data.sentiment} ({data.average_score})
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
